mapper.select:
select u.id, u.name, u.email, u.phone_number, u.birthdate, u.gender, u.nickname, u.reputation, a.user_sido, a.user_sigungu
from user u, useraddress a
where a.user_number = u.user_number and u.user_number = #{user_number}

select i.category_number
from interest i
where i.user_number = #{user_number}

select pw
from user
where user_number = #{user_number} and pw = #{pw}

update user
set pw = #{pw}
where user_number = #{user_number}

select email
from user
where email = #{email}

update user
set email = #{email}
where user_number = #{user_number}

select phone_number
from user
where phone_number = #{phone_number}

update user
set phone_number = #{phone_number}
where user_number = #{user_number}

mapper.update:
update useraddress
set user_sido = #{user_sido}, user_sigungu = #{user_sigungu}
where user_number = #{user_number}

mapper.select:
select email
from user
where email = #{email}

mapper.update:
update user
set pw = #{pw}, phone_number = #{phone_number}, email = #{email}
where id = #{id}

update useraddress
set user_sido = #{user_sido}, user_sigungu = #{user_sigungu}, user_dong = #{user_dong}
where user_number = #{user_number}
